import ImageCard from '../image/ImageCard';
import './content.css';

function Content() {
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((data) => setImages(JSON.parse(data))); 
    console.log(images);
  }, []);
  
