        rowHeight={121}>
          {images.map((item) => (
            <ImageListItem key={item.img} cols={item.cols || 1} rows={item.rows || 1}>
              <ImageCard url={item.url} title={item.title} rows={item.rows} cols={item.cols}/>
            </ImageListItem>
          ))}
          
      </ImageList> */}
      {images.map((item, index) => (
        <ImageCard key={index} url={item.url} title={item.title}/>
      ))}
    </div>
  )
}

export default Content
