import React, { useEffect, useState } from 'react';
import { Container, Row, Col } from "react-bootstrap";
import ImageCard from '../image/ImageCard';
import './content.css';

function Content() {
  const [images, setImages] = useState([]);
  const [text, setText] = useState('');
  const [btnPressed, setBtnPressed] = useState(false);

  useEffect(() => {
    console.log(text);
    setImages([]);
    fetch(`/api/${text}`)
      .then((res) => res.json())
      .then((data) => setImages(JSON.parse(data).items)); 
    setBtnPressed(false);
  }, [btnPressed]);

  return (
    <section>
            <div className="search">
              <input type="text" id="input" onChange={(e) => setText(e.target.value)}/>
              <button className='searchButton' type='button' onClick={() => setBtnPressed(true)}>Search</button>
            </div>
            <div className='content'>
              { images.map((item) => (
                  <ImageCard key={item.media.m} url={item.media.m} title={item.title}/>
              ))}
            </div>
    </section>
    
  )
}

export default Content
