import React, { useEffect, useState } from 'react';
import TextField from "@mui/material/TextField";
import ImageCard from '../image/ImageCard';

import './content.css';
import { MuiTextField } from '../MuiTextField';

function Content() {
  const [images, setImages] = useState([]);
  const [text, setText] = useState('');
  const [btnPressed, setBtnPressed] = useState(false);

  useEffect(() => {
    console.log(text);
    setImages([]);
    fetch(`/api/${text}`)
      .then((res) => res.json())
      .then((data) => setImages(JSON.parse(data).items)); 
    setBtnPressed(false);
  }, [btnPressed]);

  return (
    <div>
      <div className='main'>
        <div className="search">
          <MuiTextField />
          {/* <input type="text" id="input" onChange={(e) => setText(e.target.value)}/> */}
          <button className='serachButton' type='button' onClick={() => setBtnPressed(true)}>Search</button>
        </div>
      </div>
      <div className='content'>
        { images.map((item) => (
            <ImageCard key={item.media.m} url={item.media.m} title={item.title}/>
        ))}
      </div>
    </div>
    
  )
}

export default Content
