import React, { useEffect, useState } from 'react';
import ImageCard from '../image/ImageCard';

import './content.css';

function Content() {
  const [images, setImages] = useState([]);
  const [text, setText] = useState('');
  const [btnPressed, setBtnPressed] = useState(false);

  useEffect(() => {
    fetch(`/api/${text}`)
      .then((res) => res.json())
      .then((data) => setImages(JSON.parse(data).items)); 
    setBtnPressed(false);
  }, [btnPressed]);

  // function handleSubmit() {
  //   fetch(`/api/${text}`)
  //     .then((res) => res.json())
  //     .then((data) => setImages(JSON.parse(data).items)); 
  // }

  return (
    <div>
      <div className='main'>
        <div className="search">
          <input type="text" id="input" onChange={(e) => setText(e.target.value)}/>
          <button className='serachButton' type='button' onClick={setBtnPressed(true)}>Search</button>
        </div>
      </div>
      <div className='content'>
        { images.map((item) => (
            <ImageCard key={item.author_id} url={item.media.m} title={item.title}/>
        ))}
      </div>
    </div>
    
  )
}

export default Content
